            },
            error => {
                return callback({ success: false, message: error });
            });
    },
    getById: (id, callback) => {
        ProjectQueries.getById(id,
            response => {
                return callback({ success: true, message: response });
            },
            error => {
                return callback({ success: false, message: error });
            });
